import type { OperationStatusCode } from './apiClient';
import type { GenericObjectType } from './types';
export type Table<T> = {
    [key: string]: unknown;
    rows: Array<TableRow<T>>;
    meta: TableMeta;
    form: GenericObjectType;
};
export type TableSingle<T> = {
    [key: string]: unknown;
    row: TableRow<T>;
    meta: TableMeta;
    form: GenericObjectType;
};
export type TableRow<T> = {
    id: string;
    createdAt: Date;
    updatedAt: Date;
    createdBy: string;
    updatedBy: string;
    linked: LinkedRecord[];
    [key: string]: unknown;
} & T;
export type LinkedRecord = {
    id: string;
    ref: string;
    [key: string]: unknown;
};
export type TableMeta = {
    id: string;
    label: string;
    columns: {
        [key: string]: TableMetaColumn;
    };
    references: Reference[];
    linkedWidgets: LinkedWidget[];
};
export type TableMetaColumnUI = {
    [key: string]: unknown;
    minWidth?: number;
};
export type TableMetaColumn = {
    name: string;
    label: string;
    component: string;
    config: GenericObjectType;
    ui: TableMetaColumnUI;
};
export type Reference = {
    fromColumn: string;
    toTable: string;
    toColumn: string;
    referenceName: string;
    referenceLabel: string;
};
export type LinkedWidget = {
    label: string;
    widget: string;
    config: GenericObjectType;
};
export interface BulkInsertResult {
    rowId: string | undefined;
    success: OperationStatusCode;
    error: Error | undefined;
}
export type MaxItems = 2147483647;
export type PaginationType = 10 | 30 | 50 | 100 | MaxItems;
export type FilterParameters = {
    table: string;
    search: string;
    filter: string;
    orderBy: string;
    skip: number;
    top: PaginationType;
    select: string;
};
