export interface VideoPlayerState {
    isPlaying: boolean;
    isMuted: boolean;
    volume: number;
    duration: number;
    currentTime: number;
    playbackRate: number;
    isLoading: boolean;
}
export type UpdateVideoState = <K extends keyof VideoPlayerState>(key: K, value: VideoPlayerState[K] | ((prev: VideoPlayerState[K]) => VideoPlayerState[K])) => void;
